local plugin_id = "auto-arknights"

-- 插件启用时调用
function onEnable()
    sl.log.info("明日方舟自动安装器插件已启用")
    -- 显示明日方舟图片
    createSplashScreen()
end

-- 创建开屏动画
function createSplashScreen()
    sl.log.info("创建开屏动画")
    local splash_id = "arknightsn-splash-screen"
    
    -- 先移除可能存在的旧动画
    sl.ui.remove_html(splash_id)
    
    -- 注入CSS样式
    local css = [[
        #arknights-splash-screen {
            position: fixed !important;
            top: 0 !important;
            left: 0 !important;
            width: 100vw !important;
            height: 100vh !important;
            background: #000000 !important;
            display: flex !important;
            align-items: center !important;
            justify-content: center !important;
            z-index: 9999 !important;
            margin: 0 !important;
            padding: 0 !important;
            border: none !important;
            opacity: 1;
        }
        
        #arknights-image {
            max-width: 80% !important;
            max-height: 80% !important;
            opacity: 0;
            transition: opacity 5s ease-in-out !important;
        }
    ]]
    
    sl.ui.inject_css("splash-styles", css)
    sl.log.info("注入CSS样式")
    
    -- 创建全屏白色背景，显示图片，添加渐显效果
    local html = [[
        <div id="arknights-splash-screen">
            <img id="arknights-image" src="data:image/png;base64," alt="明日方舟开屏图片" />
            <script>
                console.log('明日方舟开屏动画显示');
                
                // 确保元素存在
                var img = document.getElementById('arknights-image');
                var screen = document.getElementById('arknights-splash-screen');
                
                if (img) {
                    // 强制重排
                    img.offsetWidth;
                    
                    // 立即触发渐显效果
                    setTimeout(function() {
                        img.style.opacity = '1';
                    }, 100);
                }
                
                // 8秒后开始渐隐
                setTimeout(function() {
                    if (screen) {
                        screen.style.opacity = '0';
                        screen.style.transition = 'opacity 1s ease-in-out';
                        
                        // 完全隐藏后移除元素
                        setTimeout(function() {
                            if (screen) {
                                screen.remove();
                            }
                        }, 1000);
                    }
                }, 8000);
            </script>
        </div>
    ]]
    
    sl.log.info("注入HTML")
    
    local result = sl.ui.inject_html(splash_id, html)
    sl.log.info("注入结果: " .. tostring(result))
end
